import logo from "./logo.svg";
import "./Ap.css";
import {useRef,useState} from "react";

function App() {
  const inputref=useRef(null);
  const [image,setImage]=useState("");
  const handleImageClick=()=>{
    inputref.current.click();
  }
  ;
  const handleImageChange=(event)=>

  {
    const file= event.target.files[0];
    console.log(file);
    setImage(file);
    setImage(event.target.files[0])
  };

  return (
    <div className="App">
      <aside className="sidemenu">
        <div className="smbutton">

          <div className="logo"> <p>COMPANY NAME</p></div>
        </div>
      </aside>
      <section className="chatbox">
        <div className="chat-log">
          <div className="c-msg">
            <div className="center">
              <div className="avtar">
                <div className="msg">hello world</div>
              </div>
            </div>
          </div>
        </div>

        <div onclick={handleImageClick} className="upload">
          {image ? <img src={URL.createObjectURL(image)}/>
            :<img src="logo192.png"/>


          }
          <input type="file" ref={inputref} onChange={handleImageChange} style={{display:"none"}}></input>

        </div>

        <div className="inpholder">
          <textarea rows="1" className="chattext"></textarea>
        </div>
      </section>
    </div>
  );
}

export default App;
