import React, { useState } from "react";
import "./App.css";
import DragDropFiles from "./components/DragDropFiles";



function App() {
  const handleSignUp = () => {
    // Implement your sign-up logic here
  };

  const handleLogin = () => {
    setIsAuthenticated(true);
  };

  const handleUpload = () => {
    // Implement your upload logic here
  };

  return (
    <div className="App">
      {/* ... */}
      <aside className="sidemenu">COMPANY NAME</aside>
      <section className="chatbox">
        <div className="newfile">
          <span>New File</span>
          <br />
          <br />
          <span>New Project</span>
        </div>

        <div className="signup">
          {isAuthenticated ? (
            uploadedImage ? (
              <button className="upload-b" onClick={handleUpload}>
                UPLOAD
              </button>
            ) : (
              <div
                className={`upload-button ${isDraggingOver ? "drag-over" : ""}`}
                onDragOver={handleDragOver}
                onDragLeave={handleDragLeave}
                onDrop={handleDrop}
              >
                {isDraggingOver ? "Drop File Here" : "Drag and Drop File Here"}
              </div>
            )
          ) : (
            <>
              <input
                type="email"
                className="sign"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <br />
              <input
                type="password"
                className="sign"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <div className="login">
                <button className="auth-button" onClick={handleSignUp}>
                  Sign Up
                </button>
                <br />
                <button className="auth-button" onClick={handleLogin}>
                  Login
                </button>
              </div>
            </>
          )}
        </div>
        <div className="inpholder">
          {/* ... */}
          {uploadedImage && (
            <div className="uploaded-image">
              <img
                style={{ width: "400px", height: "200px" }}
                src={uploadedImage}
                alt="Uploaded"
              />
              <button className="upload" onClick={handleUpload}>
                Upload
              </button>
            </div>
          )}

          <textarea rows="1" className="chattext"></textarea>
        </div>
      </section>
    </div>
  );
}

export default App;
