import React, { useState } from "react";
import "./App.css";

function App() {
  const [uploadedImage, setUploadedImage] = useState(null);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [newFileName, setNewFileName] = useState("");
  const [newFolderName, setNewFolderName] = useState("");

  const handleImageChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setUploadedImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSignUp = () => {
    // Implement your sign up logic here
  };

  const handleLogin = () => {
    setIsAuthenticated(true);
  };

  const handleUpload = () => {
    // Implement your upload logic here
  };

  const handleCreateFile = () => {
    // Implement your create file logic here
  };

  const handleCreateFolder = () => {
    // Implement your create folder logic here
  };

  return (
    <div className="App">
      {/* ... */}
      <aside className="sidemenu">COMPANY NAME</aside>
      <section className="chatbox">
        {/* ... */}
        <div className="signup">
          {isAuthenticated ? (
            uploadedImage ? (
              <button className="upload-b" onClick={handleUpload}>
                UPLOAD
              </button>
            ) : (
              <>
                <label htmlFor="file-input" className="upload-button">
                  Choose File
                </label>
                <input
                  type="file"
                  accept="image/*"
                  className="file-input"
                  id="file-input"
                  onChange={handleImageChange}
                />
              </>
            )
          ) : (
            <>
              {/* ... */}
            </>
          )}
          {/* ... */}
          <div className="create-options">
            <input
              type="text"
              placeholder="New File Name"
              value={newFileName}
              onChange={(e) => setNewFileName(e.target.value)}
            />
            <button className="auth-button" onClick={handleCreateFile}>
              Create File
            </button>
            <input
              type="text"
              placeholder="New Folder Name"
              value={newFolderName}
              onChange={(e) => setNewFolderName(e.target.value)}
            />
            <button className="auth-button" onClick={handleCreateFolder}>
              Create Folder
            </button>
          </div>
        </div>
        <div className="inpholder">
          {/* ... */}
          {uploadedImage && (
            <div className="uploaded-image">
              <img
                style={{ width: "400px", height: "200px" }}
                src={uploadedImage}
                alt="Uploaded"
              />
              <button className="upload" onClick={handleUpload}>
                Upload
              </button>
            </div>
          )}
          {/* ... */}
        </div>
      </section>
    </div>
  );
}

export default App;
